tree.attribute:
magenta:invoice_state == False and invoiceable != False;
                        blue:invoice_state in ('draft', 'proforma', 'proforma2') and invoiceable != False;
                        green:invoice_state == 'open' and invoiceable != False;
                        black:invoice_state == 'paid' and invoiceable != False;
                        red:invoice_state == 'cancel' and invoiceable != False
